<template>
  <!-- 登录模块 -->
  <div class="login">
    <van-nav-bar title="登录" left-arrow @click-left="add"></van-nav-bar>
    <div class="logo"></div>
    <div class="loginbox">
      <van-cell-group>
        <!-- 手机号输入框 -->
        <van-field
          v-model="phone"
          @blur="phonefiy"
          type="number"
          autosize
          placeholder="请输入手机号"
          maxlength="11"
        />
        <!-- 密码输入框 -->
        <van-field v-model="password" @blur="passwordfiy" type="password" placeholder="请输入密码" />
      </van-cell-group>
      <!-- 忘记密码 -->
      <p class="forget" @click="forget">忘记密码</p>
      <!-- 登录按钮 -->
      <button class="loginbtn">登录</button>
      <!-- 创建账号 -->
      <p class="loginreg" @click="regist">创建新账号</p>
      <!-- 其他登录 -->
      <div class="other">
        <p class="otlogin">其他登录方式</p>
        <div class="otimg">
          <!-- 微信登录 -->
          <img
            class="wx"
            src="data:image/png;base64,"
            alt
          />
          <!-- qq登录 -->

          <img
            class="qq"
            src="data:image/png;base64,"
            alt
          />
        </div>
      </div>
    </div>
  </div>
</template>
<script>
export default {
  data() {
    return {
    //   手机号
      phone: "",
    //   密码
      password: ""
    };
  },
  methods: {
    //   返回上一页
    add() {
      this.$router.go("-1");
    },
    // 手机号正则表达式
    phonefiy() {
      var _this = this;
      var reg = /^(?:(?:\+|00)86)?1[3-9]\d{9}$/;
      if (_this.phone == "") {
        this.$toast("手机号格式不能为空！");
      } else {
        if (!reg.test(_this.phone)) {
          this.$toast("手机号格式不正确");
        }
      }
      return true;
    },
    // 密码正则表达式
    passwordfiy() {
      var _this = this;
      var reg = /^.*(?=.{8,})(?=.*\d)(?=.*[A-Za-z]).*$/;
      if (_this.password == "") {
        this.$toast("密码不能为空！");
      } else {
        if (!reg.test(_this.password)) {
          this.$toast("密码格式不正确！6位以上字母和数组组合");
        }
      }
    },
    regist(){
        this.$router.push('/regist')
    },
    forget(){
        this.$router.push('/forget')
    }
  }
};
</script>
